@app.route('/get_items/', methods=['POST'])
def get_items():
    if request.headers['Content-Type'] == 'application/json' and request.method == 'POST':
        inputs =request.json
        farsi2eng_number = { 0:'۰', 1:"۱",  2:"۲",  3:"۳",  4:"۴",  5:"۵",  6:"۶", 7:"۷",  8:"۸", 9:"۹"}
        # options = {'size': (2,3), 'regions': []}
        if 'type' in inputs:
            type=inputs['type']
        if 'size' in inputs:
            min_size=''.join([farsi2eng_number[int(ch)] for ch in str(inputs['size'][0])])
            max_size=''.join([farsi2eng_number[int(ch)] for ch in str(inputs['size'][1])])
        regions=[]
        if 'regions' in inputs:
            for idx,region in enumerate(inputs['regions']):
                regions.append("location='"+ 'تهران، '+inputs['regions'][idx]+"'")

        if type=='buy':
            return jsonify(get_items_buy('size>"{}" and size<"{}" and ({})'.format(min_size, max_size,' or '.join(regions))))
        else:
            return jsonify(get_items_rent('size>"{}" and size<"{}" and ({})'.format(min_size, max_size,' or '.join(regions))))


